mapper.select:
SELECT

FROM
cms_article a

    AND a.is_publish=#{YES_PUBLISH} AND a.del_flag=#{DEL_FLAG_NORMAL}
    
        and a.id = #{id}

SELECT cat.content as "textId.content",
FROM cms_article a LEFT JOIN cms_article_text cat ON a.id = cat.id
AND a.is_publish=#{YES_PUBLISH} AND a.del_flag=#{DEL_FLAG_NORMAL}
and a.id = #{id}

mapper.update:
UPDATE cms_article
title=#{title}, link=#{link}, color=#{color}, title_image=#{titleImage}, keywords=#{keywords}, weight=#{weight}, posid=#{posid}, updateDate=#{updateDate}, remarks=#{remarks}
WHERE id = #{id}

UPDATE cms_article SET
  is_publish = #{YES_PUBLISH}
WHERE id = #{id}

UPDATE cms_article SET
  del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
